using System;
using System.Net;
using MailKit.Net.Smtp;
using AutoMapper;
using CrossFitWOD.Models;
using MailKit;
using MailKit.Security;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Options;
using MimeKit;
using CrossFitWOD.Interfaces;

namespace CrossFitWOD.Controllers
{
    public class EmailService: IEmailService
    {
        private readonly EmailSettings _EmailSettings;

        public EmailService(IOptions<EmailSettings> mailSettings)
        {
            _EmailSettings = mailSettings.Value;
        }

        public async Task SendPlainTextMessageAsync(EmailRequest emailRequest)
        {
            var email = new MimeMessage
            {
                Sender = MailboxAddress.Parse(_EmailSettings.Email),
                Subject = "Message Request!",
            };
            email.To.Add(MailboxAddress.Parse(_EmailSettings.ToEmail));

            var builder = new BodyBuilder();
            builder.HtmlBody = $"<strong>This is: </strong>{emailRequest.FirstName} {emailRequest.LastName}" +
                $"<br/>" +
                $"<strong>Email: </strong>{emailRequest.Email}" +
                $"<br/>" +
                $"<strong>Message: </strong>" +
                $"<p>{emailRequest.Message}</p>";

            email.Body = builder.ToMessageBody();

            using var smtp = new SmtpClient();
            smtp.Connect(_EmailSettings.Host, _EmailSettings.Port, SecureSocketOptions.StartTls);
            smtp.Authenticate(_EmailSettings.Email, _EmailSettings.Password);

            await smtp.SendAsync(email);

            smtp.Disconnect(true);
        }

        public async Task SendVerificationEmailAsync(string link, string receiver)
        {
            var draft = new MimeMessage
            {
                Sender = MailboxAddress.Parse(_EmailSettings.Email),
                Subject = "Email Verification Required",
            };
            draft.To.Add(MailboxAddress.Parse(receiver));

            var builder = new BodyBuilder();
            /*builder.HtmlBody =
                $"<strong>Please click on the link below to verify email prior to login.</strong>" +
                $"<br/>" +
                $"{link}";*/

            var temp =
                $"<div style=\"width: 100%; display: flex; flex-direction: column; padding: 2em; background-color: white; color: black;\">\n" +
                    $"<div style=\"min-width: 475px; margin: 0 auto; display: flex; flex-direction: column;\">\n" +
                        //$"<img style=\"width: 250px; margin: 0 auto;\" title=\"Verify Email\" src=\"data:image/png;base64,\">\n" +
                        $"<p>Thank you for registering!\n" +
                        $"<br/>\n" +
                        $"Before we get started, click the link below to confirm your email and complete your profile.\n" +
                        $"<br/>\n" +
                        $"This will allow you to login for the first time!\n" +
                        $"</p>\n" +
                        $"<a target=\"_blank\" style=\"color: #fff;background-color: #44890D;border-color: #6c757d;font-weight: 400;text-align: center;vertical-align: middle;cursor: pointer;border: 1px solid transparent;padding: 0.5rem 0.75rem;line-height: 0.5em;border-radius: 0.25rem;text-decoration: none; display:inline-block; line-height: 1.5rem; margin-bottom: .75em; width: 200px;\" href=\"{link}\">Confirm My Email!</a>\n" +
                        $"<p>\n" +
                        $"<strong style=\"font-size: 1.2rem; letter-spacing: .5px;\">Important Note:</strong>\n" +
                        $"<br/>\n" +
                        $"This email was uniquely generated to your account.\n" +
                        $"<br/>\n" +
                        $"Please do not forward or share this email.\n" +
                        $"</p>\n" +
                    $"</div>\n" +
